mapper.select:
select * 
from planMap 
where placeName=#{placeName, javaType=String}

select count(*) 
from planMap 
where planNo=#{planNo, javaType=int}
and	id=#{id, javaType=String}
and placeName=#{placeName, javaType=String}

mapper.delete:
delete from planMap 
where planNo=#{planNo, javaType=int}
and id=#{id, javaType=String} 
and placeName=#{placeName, javaType=String}

mapper.select:
select count(*) 
from planMap 
where planNo=#{planNo, javaType=int} 
and placeName=#{placeName, javaType=String}

mapper.update:
update planMap set pickCount=#{pickCount, javaType=int} 
where planNo=#{planNo, javaType=int} 
and placeName=#{placeName, javaType=String}
